sqlMap.select:
/* ErpOrgchartDAO.selectEmpDeptInfo - mariaDB */ select * from $dbNeos$t_co_emp_dept d left outer join $dbNeos$t_co_emp_comp c on d.comp_seq = c.comp_seq and d.emp_seq = c.emp_seq where 1=1
and d.dept_seq = #deptSeq#
and c.erp_emp_seq = #erpEmpSeq# and c.erp_comp_seq = #erpCompSeq#

/* ErpOrgchartDAO.selectTmpCompList - mariaDB */ select * from $DB_NEOS$t_tmp_comp c left outer join $DB_NEOS$t_tmp_comp_multi cm on cm.sync_seq = #syncSeq# and c.comp_seq = cm.comp_seq where c.sync_seq = #syncSeq#
and c.result_code = #resultCode#
limit $startNum$, $endNum$
