sqlMap.select:
/* OnefficeDao.getSearchDocumentList - oracle */
SELECT distinct b.doc_no AS "doc_no", doc_type AS "doc_type", folder_no AS "folder_no", doc_name AS "doc_name", content_size AS "content_size", readonly AS "readonly", important AS "important", keyword AS "keyword", TO_CHAR(reg_date,'YYYY-MM-DD hh24:mi:ss') as "reg_date", TO_CHAR(mod_date,'YYYY-MM-DD hh24:mi:ss') as "mod_date", TO_CHAR(access_date,'YYYY-MM-DD hh24:mi:ss') as "access_date", case when NVL(TRIM(b.security_key),'') IS NULL then '0' else '1' END as "security_key", (select COUNT(*) from $DB_NEOS$oneffice_share kk where kk.doc_no=b.doc_no) AS "share_count", b.thumbnail as "thumbnail" FROM $DB_NEOS$oneffice_document b JOIN $DB_NEOS$t_co_group g ON g.group_seq = #groupSeq# LEFT JOIN $DB_NEOS$oneffice_share a ON a.doc_no = b.doc_no LEFT JOIN $DB_NEOS$t_co_emp_comp ec on ec.work_status = '999' and ec.use_yn = 'Y' and ec.emp_seq = #empSeq# LEFT JOIN $DB_NEOS$t_co_dept d on d.dept_seq = ec.dept_seq and d.use_yn = 'Y' WHERE ( (a.share_type='0' AND a.share_id = #empSeq#) OR (a.share_type='1' AND ('|' || d.path || '|') like ('%|' || a.share_id || '|%')) OR a.share_type='9' OR #empSeq# IS NULL ) and deleted='0' and (doc_name like '%$keyword$%' or keyword like '%$keyword$%') order by doc_type ASC, doc_name ASC
select doc_no AS "doc_no", doc_type AS "doc_type", folder_no "folder_no", doc_name AS "doc_name", content_size AS "content_size", readonly AS "readonly", important AS "important", keyword AS "keyword", TO_CHAR(reg_date,'YYYY-MM-DD hh24:mi:ss') as "reg_date", TO_CHAR(mod_date,'YYYY-MM-DD hh24:mi:ss') as "mod_date", TO_CHAR(access_date,'YYYY-MM-DD hh24:mi:ss') as "access_date", case when NVL(TRIM(security_key),'') IS NULL then '0' else '1' END as security_key, (select COUNT(*) from $DB_NEOS$oneffice_share kk where kk.doc_no=b.doc_no) AS "share_count", b.thumbnail as "thumbnail" from $DB_NEOS$oneffice_document b where owner_id=#empSeq# and deleted='0' and (doc_name like '%$keyword$%' or keyword like '%$keyword$%') order by doc_type ASC, doc_name ASC

sqlMap.insert:
/* OnefficeDao.shareDocument - oracle */
insert into $DB_NEOS$oneffice_share (owner_id, doc_no, share_id, share_type, share_perm, share_date)
values(#empSeq#, #doc_no#, #share_id#, #share_type#, #share_perm#, SYSDATE)

/* OnefficeDao.accessUserFunction - oracle */
insert into $DB_NEOS$oneffice_usage_log (user_id, os_type, app_type, call_url, call_date) VALUES
(#user_id#, #os_type#, #app_type#, #call_url#, SYSDATE)

sqlMap.select:
/* OnefficeDao.getSecurityInfo - oracle */
SELECT doc_no as "doc_no", DECODE(NVL(TRIM(security_key),''), NULL, 0, 1) AS "status" FROM $DB_NEOS$oneffice_document WHERE doc_no = #doc_no#
SELECT doc_no AS "doc_no", case when NVL(TRIM(security_key),'') IS NULL then '0' when NVL(security_key,'') = password(#security_key#) then '2' when NVL(security_key,'') != password(#security_key#) then '3' else '1' end AS "status" FROM $DB_NEOS$oneffice_document WHERE doc_no = #doc_no#

sqlMap.insert:
MERGE INTO $DB_NEOS$oneffice_user_env USING DUAL ON (user_id = #empSeq#) WHEN MATCHED THEN UPDATE SET user_env = #user_env# , last_update = SYSDATE WHEN NOT MATCHED THEN INSERT ( user_id , user_env , last_update
, user_agree , user_agree_date
)
VALUES ( #empSeq# , #user_env# , SYSDATE
, #user_agree# , SYSDATE
)

sqlMap.select:
