sqlMap.select:
/* ErpOrgchartDAO.selectTmpDeptTotalCount - oracle */ SELECT '0' as result_code, COUNT(*) AS result_code_cnt from $DB_NEOS$t_tmp_dept A inner join $DB_NEOS$t_tmp_dept_multi B on A.dept_seq = B.dept_Seq where A.sync_seq = #syncSeq# and B.sync_seq = #syncSeq# and A.result_code = B.result_code and (A.result_code = '0' or B.result_code = '0') and b.lang_code = 'kr'
union all
SELECT '20' as result_code, COUNT(*) AS result_code_cnt from $DB_NEOS$t_tmp_dept A inner join $DB_NEOS$t_tmp_dept_multi B on A.dept_seq = B.dept_Seq where A.sync_seq = #syncSeq# and B.sync_seq = #syncSeq# and A.result_code != B.result_code and (A.result_code = '20' or B.result_code = '20') and b.lang_code = 'kr'

/* ErpOrgchartDAO.selectTmpEmpTotalCount - oracle */ SELECT replace(result_code,' ','') as result_code, COUNT(*) AS result_code_cnt from $DB_NEOS$t_tmp_emp where sync_seq = #syncSeq# and result_code in ('0','20', '2', '7')
and emp_seq in ($noEmpList$)
group by result_code

sqlMap.update:
/* ErpOrgchartDAO.updateDeptInfo - oracle */ MERGE INTO $DB_NEOS$t_co_dept A USING t_tmp_dept B ON (A.dept_seq = B.dept_seq AND B.sync_seq = #syncSeq# AND (B.result_code = '20' or B.result_code = '30') AND A.comp_seq = #compSeq#) WHEN MATCHED THEN UPDATE SET A.biz_seq = B.biz_seq, A.parent_dept_seq = B.parent_dept_seq, A.tel_num = B.tel_num, A.fax_num = B.fax_num, a.order_num = b.order_num,
A.use_yn = B.use_yn,
A.path = B.path

/* ErpOrgchartDAO.updateDeptMulti - mariaDB */
MERGE INTO $DB_NEOS$t_co_dept_multi USING DUAL ON (dept_seq = #deptSeq# AND lang_code = #langCode#) WHEN MATCHED THEN UPDATE SET dept_name = #deptName#, path_name = #pathName#, use_yn = #useYn# WHEN NOT MATCHED THEN INSERT ( dept_seq, lang_code, group_seq, comp_seq, biz_seq, dept_name, dept_display_name, sender_name, addr, detail_addr, path_name, use_yn, create_seq )
VALUES ( #deptSeq#, #langCode#, #groupSeq#, #compSeq#, #bizSeq#, #deptName#, #deptDisplayName#, #senderName#, #addr#, #detailAddr#, #pathName#, #useYn#, #createSeq# )

sqlMap.select:
/* ErpOrgchartDAO.selectTmpCompList - oracle */
SELECT t2.sync_seq , t2.group_seq , t2.comp_seq , t2.comp_cd , t2.parent_comp_seq , t2.login_type , t2.comp_regist_num , t2.comp_num , t2.standard_code , t2.erp_use , t2.tel_num , t2.fax_num , t2.homepg_addr , t2.comp_mail_url , t2.comp_domain , t2.email_addr , t2.email_domain , t2.zip_code , t2.sms_use , t2.sms_id , t2.sms_passwd , t2.native_lang_code , t2.order_num , t2.use_yn , t2.ea_type , t2.create_seq , t2.create_date , t2.modify_seq , t2.modify_date , t2.comp_email_yn , t2.erp_use_yn , t2.sms_use_yn , t2.erp_comp_seq , replace(t2.result_code,' ','') as result_code , t2.lang_code , t2.comp_name , t2.comp_display_name , t2.owner_name , t2.sender_name , t2.biz_condition , t2.item , t2.addr , t2.detail_addr , t2.comp_nickname , t2.erp_comp_name FROM ( SELECT t1.sync_seq , t1.group_seq , t1.comp_seq , t1.comp_cd , t1.parent_comp_seq , t1.login_type , t1.comp_regist_num , t1.comp_num , t1.standard_code , t1.erp_use , t1.tel_num , t1.fax_num , t1.homepg_addr , t1.comp_mail_url , t1.comp_domain , t1.email_addr , t1.email_domain , t1.zip_code , t1.sms_use , t1.sms_id , t1.sms_passwd , t1.native_lang_code , t1.order_num , t1.use_yn , t1.ea_type , t1.create_seq , t1.create_date , t1.modify_seq , t1.modify_date , t1.comp_email_yn , t1.erp_use_yn , t1.sms_use_yn , t1.erp_comp_seq , t1.result_code , t1.lang_code , t1.comp_name , t1.comp_display_name , t1.owner_name , t1.sender_name , t1.biz_condition , t1.item , t1.addr , t1.detail_addr , t1.comp_nickname , t1.erp_comp_name , ROWNUM AS RNUM FROM (
SELECT ta.sync_seq , ta.group_seq , ta.comp_seq , ta.comp_cd , ta.parent_comp_seq , ta.login_type , ta.comp_regist_num , ta.comp_num , ta.standard_code , ta.erp_use , ta.tel_num , ta.fax_num , ta.homepg_addr , ta.comp_mail_url , ta.comp_domain , ta.email_addr , ta.email_domain , ta.zip_code , ta.sms_use , ta.sms_id , ta.sms_passwd , ta.native_lang_code , ta.order_num , ta.use_yn , ta.ea_type , ta.create_seq , ta.create_date , ta.modify_seq , ta.modify_date , ta.comp_email_yn , ta.erp_use_yn , ta.sms_use_yn , ta.erp_comp_seq , replace(ta.result_code,' ','') as result_code , tb.lang_code , tb.comp_name , tb.comp_display_name , tb.owner_name , tb.sender_name , tb.biz_condition , tb.item , tb.addr , tb.detail_addr , tb.comp_nickname , tb.erp_comp_name FROM $DB_NEOS$t_tmp_comp ta LEFT OUTER JOIN $DB_NEOS$t_tmp_comp_multi tb ON ta.comp_seq = tb.comp_seq AND tb.sync_seq = #syncSeq# WHERE ta.sync_seq = #syncSeq#
AND ta.result_code = #resultCode#
) t1 WHERE ROWNUM <= $endNum$ ) t2 WHERE t1.RNUM >= $startNum$

/* ErpOrgchartDAO.selectTmpCompTotalCount - oracle */ SELECT '0' as result_code, COUNT(*) AS result_code_cnt from $DB_NEOS$t_tmp_comp A inner join $DB_NEOS$t_tmp_comp_multi B on A.comp_seq = B.comp_seq where A.sync_seq = #syncSeq# and B.sync_seq = #syncSeq# and A.result_code = B.result_code and (A.result_code = '0' or B.result_code = '0') and b.lang_code = 'kr'
union all
SELECT '20' as result_code, COUNT(*) AS result_code_cnt from $DB_NEOS$t_tmp_comp A inner join $DB_NEOS$t_tmp_comp_multi B on A.comp_seq = B.comp_seq where A.sync_seq = #syncSeq# and B.sync_seq = #syncSeq# and A.result_code != B.result_code and (A.result_code = '20' or B.result_code = '20') and b.lang_code = 'kr'

/* ErpOrgchartDAO.selectEmpResignList - oracle */ select ce.emp_seq as "empSeq", e.group_seq as "groupSeq", e.login_id as "loginId", e.emp_num as "empNum", e.erp_emp_num as "erpEmpNum", e.email_addr as "emailAddr", e.login_passwd as "loginPasswd", e.appr_passwd as "apprPasswd", e.passwd_date as "passwdDate", e.passwd_input_fail_count as "passwdInputFailCount", e.pay_passwd as "payPasswd", e.passwd_status_code as "passwdStatusCode", e.mobile_use_yn as "mobileUseYn", e.messenger_use_yn as "messengerUseYn", e.check_work_yn as "checkWorkYn", e.shift as "shift", e.job_code as "jobCode", e.status_code as "statusCode", e.main_comp_seq as "mainCompSeq", e.duty_code as "dutyCode", e.position_code as "positionCode", e.native_lang_code as "nativeLangCode", e.license_check_yn as "licenseCheckYn", e.join_day as "joinDay", e.resign_day as "resignDay", e.gender_code as "genderCode", e.bday as "bday", e.lunar_yn as "lunarYn", e.work_status as "workStatus", e.home_tel_num as "homeTelNum", e.mobile_tel_num as "mobileTelNum", e.wedding_yn as "weddingYn", e.wedding_day as "weddingDay", e.private_yn as "privateYn", e.zip_code as "zipCode", e.pic_file_id as "picFileId", e.sign_file_id as "signFileId", e.use_yn as "useYn", e.ls_role_id as "lsRoleId", e.create_seq as "createSeq", e.create_date as "createDate", e.modify_seq as "modifySeq", e.modify_date as "modifyDate" from $DB_NEOS$t_tmp_emp e
LEFT OUTER JOIN $DB_NEOS$t_co_emp ce ON e.erp_emp_num = ce.erp_emp_num and e.emp_seq = ce.emp_seq where sync_seq = #syncSeq# AND result_code = '7'
and e.emp_seq in ($noEmpList$)

sqlMap.update:
merge into $DB_NEOS$t_co_emp_comp a using ( select a.work_status, b.comp_seq, b.dept_Seq, a.emp_seq, a.use_yn from $DB_NEOS$t_tmp_emp a inner join $DB_NEOS$t_tmp_emp_dept b on a.sync_seq = #syncSeq# and b.sync_seq = #syncSeq# and a.emp_seq = b.emp_Seq and a.result_code in ('0','20')
and a.emp_seq in ($noEmpList$)
)B on (a.emp_seq = b.emp_seq and a.comp_seq = b.comp_seq) when matched then update set a.work_status = B.work_status , a.use_yn = B.use_yn
