sqlMap.update:
/* AttachFileUpload.updateAttachFileDetail - oracle */
UPDATE $DB_NEOS$t_co_atch_file_detail SET use_yn = #useYn# WHERE file_id = #fileId#
AND file_sn IN ($fileSn$)

