sqlMap.select:
/* DeptManage.selectDept - oracle */
SELECT A.* , B.* , (SELECT dept_name FROM t_co_dept_multi WHERE dept_seq = a.parent_dept_seq AND lang_code = #langCode#) AS parentDeptName FROM t_co_dept a , t_co_dept_multi b WHERE a.dept_seq = b.dept_seq AND a.dept_seq = #deptSeq# AND b.lang_code =#langCode#
AND a.comp_seq = #compSeq#

/* DeptManage.selectParentDept - oracle */
SELECT a.group_seq, a.comp_seq, a.biz_seq, a.dept_seq, a.parent_dept_seq, $DB_NEOS$FN_GetMultiLang(#langCode#,b.dept_name_multi) as dept_name FROM $DB_NEOS$t_co_dept a, $DB_NEOS$v_t_co_dept_multi b WHERE a.dept_seq = b.dept_seq AND a.parent_dept_seq = (select aa.parent_dept_seq from $DB_NEOS$t_co_dept aa where aa.dept_seq = #deptSeq# AND ROWNUM = 1) AND a.comp_seq = #compSeq# order by a.order_num asc
SELECT '' AS group_seq, comp_seq, '' AS biz_seq, '0' AS dept_seq, '0' AS parent_dept_seq, $DB_NEOS$FN_GetMultiLang(#langCode#,comp_name_multi) AS dept_name FROM $DB_NEOS$v_t_co_comp_multi WHERE comp_seq = #compSeq# AND ROWNUM = 1

sqlMap.insert:
/* DeptManage.insertDept - oracle */ MERGE INTO $DB_NEOS$t_co_dept USING DUAL ON (dept_seq = #deptSeq#) WHEN MATCHED THEN UPDATE SET dept_cd = #deptCd# , group_seq = #groupSeq# , comp_seq = #compSeq# , biz_seq = #bizSeq# , parent_dept_seq = #parentDeptSeq# , tel_num = #telNum# , fax_num = #faxNum# , homepg_addr = #homepgAddr# , zip_code = #zipCode# , vir_dept_yn = #virDeptYn# , team_yn = #teamYn# , path = CASE WHEN #parentDeptSeq# = '0' THEN #deptSeq# ELSE $DB_NEOS$get_dept_path('|', #parentDeptSeq#, #groupSeq#) || '|' || #deptSeq# END , ptype = #ptype# , dept_level = $DB_NEOS$get_dept_level(#deptSeq#, #groupSeq#) , order_num = #orderNum# , use_yn = #useYn# , modify_seq = #modifySeq# , modify_date = SYSDATE , display_yn = #displayYn#
, dept_manager = #captainSeqInfo#
WHEN NOT MATCHED THEN INSERT ( dept_seq , dept_cd , group_seq , comp_seq , biz_seq , parent_dept_seq , tel_num , fax_num , homepg_addr , zip_code , susin_yn , vir_dept_yn , team_yn , native_lang_code , path , dept_level , ptype , order_num , use_yn , create_seq , create_date , modify_seq , modify_date , display_yn
, dept_manager
)
VALUES ( #deptSeq# , #deptCd# , #groupSeq# , #compSeq# , #bizSeq# , #parentDeptSeq# , #telNum# , #faxNum# , #homepgAddr# , #zipCode# , #susinYn# , #virDeptYn# , #teamYn# , #nativeLangCode# , $DB_NEOS$get_dept_path('|',#deptSeq#, #groupSeq#) , $DB_NEOS$get_dept_level(#deptSeq#, #groupSeq#) , #ptype# , #orderNum# , #useYn# , #createSeq# , SYSDATE , #modifySeq# , SYSDATE , #displayYn#
, #captainSeqInfo#
)

sqlMap.select:
/* DeptManage.selectDeptCd - oracle */ select dept_cd from $DB_NEOS$t_co_dept where comp_seq = #compSeq# and dept_cd = #deptCd#
AND dept_seq != #deptSeq#
AND ROWNUM = 1

/* DeptManage.selectBizCd - oracle */ select biz_cd from $DB_NEOS$t_co_biz where comp_seq = #compSeq# and biz_cd = #deptCd#
AND biz_seq != #deptSeq#
AND ROWNUM = 1
