sqlMap.update:
/* OrgAdapterManage.updateEmpDeptMainDeptYn - oracle */
UPDATE $DB_NEOS$t_co_emp_dept set main_dept_yn='N'
where comp_seq=#compSeq# and dept_seq != #deptSeq# and emp_seq=#empSeq#

/* OrgAdapterManage.updateEmpDeptOrderText - oracle */ MERGE INTO t_co_emp_dept T USING( select ( LPAD(DECODE(NVL(POSI.order_num,''), '','9999999999',POSI.order_num),10,'0')||'|'|| LPAD(DECODE(NVL(DUTY.order_num,''), '','9999999999',DUTY.order_num),10,'0')||'|'|| LPAD(DECODE(NVL(A.order_num,''), '','9999999999',A.order_num),10,'0')||'|'|| NVL(LPAD(DECODE(NVL(TO_CHAR(E.join_day, 'YYYYMMDD'),'99999999'), '00000000','99999999',TO_CHAR(E.join_day, 'YYYYMMDD')),8,'0'),'99999999')||'|'|| NVL(vem.emp_name,'') ) AS order_text FROM t_co_emp_dept A LEFT JOIN $DB_NEOS$t_co_comp_duty_position POSI ON A.position_code = POSI.dp_seq AND POSI.dp_type='POSITION' LEFT JOIN $DB_NEOS$t_co_comp_duty_position DUTY ON A.duty_code = DUTY.dp_seq AND DUTY.dp_type='DUTY' LEFT JOIN $DB_NEOS$t_co_emp E ON A.emp_seq = e.emp_seq LEFT JOIN $DB_NEOS$v_t_co_emp_multi vem ON A.emp_seq = vem.emp_seq and vem.lang_code = 'kr' WHERE
A.dept_seq = #deptSeq# AND A.emp_seq = #empSeq#
)V ON (T.dept_seq = #deptSeq# AND T.emp_seq = #empSeq#) WHEN MATCHED THEN UPDATE SET T.order_text = V.order_text

/* OrgAdapterManage.setEmpCompCheckWorkYn - oracle */
update $DB_NEOS$t_co_emp_comp set check_work_yn='N'
where comp_seq != #compSeq# and emp_seq = #empSeq#

sqlMap.insert:
/* OrgAdapterManage.insertEmpBaseAuth - oracle */ insert into $DB_NEOS$t_co_auth_relate select a.author_code, a.author_type, #compSeq#, #deptSeq#, #empSeq# from $DB_NEOS$t_co_authcode a left join $DB_NEOS$t_co_auth_relate b on a.author_code=b.author_code and b.comp_seq=#compSeq# and b.dept_seq=#deptSeq# and b.emp_seq=#empSeq# where b.emp_seq is null and a.comp_seq=#compSeq#
and a.author_base_yn='Y'
and a.author_code=#authorCode#

